let submit=document.querySelector(".email-submit");const messages={inputFieldEmpty:"Input field empty",invalidEmail:"Please enter valid email address",validationSuccessful:"You have subscribed successfully"},validateEmail=e=>String(e).toLowerCase().match(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/),submitEmail=()=>{let e=document.querySelector(".email-input"),s=document.querySelector(".error-div"),i=document.querySelector(".warning-icon");return 0===e.value.length?(s.classList.remove("success"),s.innerHTML=messages.inputFieldEmpty,e.classList.add("error"),i.classList.add("error"),void console.log("empty")):null===validateEmail(e.value)?(s.classList.remove("success"),s.innerHTML=messages.invalidEmail,e.classList.add("error"),i.classList.add("error"),void console.log("invalid")):null!==validateEmail(e.value)?(s.innerHTML=messages.validationSuccessful,s.classList.add("success"),e.classList.remove("error"),void i.classList.remove("error")):void 0};submit.addEventListener("click",submitEmail);